define(['jquery','prototype'],function(jQuery,prototype){var EC_Messenger=new Class.create();EC_Messenger.prototype={blockArea:null,types:null,initialize:function(){this.blockArea=$('easyconf-messages').down();this.types=new Array('message message-success','message message-notice','message message-error');},message:function(txt,type){var cls=this.types[type];var msg=new Element('li',{'class':cls}).update('<ul><li><span>'+txt+'</span></li></ul>');this.blockArea.insert(msg,{position:"bottom"});},clear:function(){this.blockArea.update('');}}
return EC_Messenger;});